// eslint-disable-next-line
import React, { useState } from "react";
import "../Home/HomeCSS/HomeMain.css";
import { BrowserRouter as Router } from "react-router-dom";
// Material UI Imports
import { Button } from "@material-ui/core";
import Modal from "@material-ui/core/Modal";

import Body2 from "./Body2";
// Image Imports
import photo from "./HomeImages/Star Logo 07-2.png";
import flag1 from "./HomeImages/Maori flag.png";
import flag2 from "./HomeImages/NZ Flag.png";
import user from "./HomeImages/Projects 01.png";
import PrepYoung from "./HomeImages/Main/Group 4.png";
import rectangleImage from "..//DavidImages/Group 2.png";
import lionKing from "..//DavidImages/lionKing.png";
import baseball from "..//DavidImages/baseball.png";
import cartoon from "..//DavidImages/cartoon.png";
import alan from "../homePage/DavidImages/alan.png";



import { Teacher, Student } from "./Login-Signup";

const Home = () => {
  const [open, setOpen] = useState(false);
  const [open2, setOpen2] = useState(false);

  const handleOpen = () => {
    setOpen(true);
  };

  const handleClose = () => {
    setOpen(false);
    setOpen2(false);
  };

  const handleOpen2 = () => {
    setOpen2(true);
  };

  const handleClick = () => {
    if (open === true) {
      setOpen(false);
      setOpen2(true);
    } else if (open2 === true) {
      setOpen2(false);
      setOpen(true);
    }
  };

  return (
    <div>
      <Modal open={open} onBackdropClick={handleClose}>
        <Teacher
          handleClick={handleClick}
          handleClose={handleClose}
          onBackdropClick={handleClose}
        />
      </Modal>

      <Modal open={open2} onBackdropClick={handleClose}>
        <Student
          handleClick={handleClick}
          handleClose={handleClose}
          onBackdropClick={handleClose}
        />
      </Modal>

      <div className="topNav">
        <Router>
          <div className="logo">
            <img className="img-main" src={photo} alt="logo" />
          </div>

          <div className="navLinks">
            <ul>
              <a
                href=""
                className="navBar-link"
                target="blank"
                rel="noopener noreferrer"
              >
                <li>Home</li>
              </a>
              <a href="" className="navBar-link mid-link">
                <li>Features</li>
              </a>
              <a href="" className="navBar-link">
                <li>Teachers</li>
              </a>
            </ul>
          </div>
          <div className="sign">
            <div className="langDiv">
              <p className="langNav">
                lang
                <img className="img-flag langNav" src={flag2} alt="logo" />
                <img className="img-flag langNav" src={flag1} alt="logo" />
              </p>
            </div>

            <p>
              <img className="usericon" src={user} alt="logo" />
              <button className="modalOpenButtons" onClick={handleOpen}>
                SIGN UP
              </button>{" "}
              |{" "}
              <button className="modalOpenButtons" onClick={handleOpen2}>
                LOG IN
              </button>
            </p>
          </div>
        </Router>
      </div>

      <div className="header">
        <img className="prep-young" src={PrepYoung} alt="header" />
      </div>
      <Body2 />

      <div className="carousel">
      <div className="row">
        <div className="col-lg-6">
          <div className="firstBody-container">
            <div className="body1-caption">
              <h1>What we offer</h1>
              <p className="body1-p">
                The Creative Problem Solving programme is series of <br></br>
                digital creation projects aimed to encourage self- <br></br>
                motivation and student agency, designed by New <br></br>
                Zealandâ€™s leading IT industry experts and schools.
              </p>
              <div className="rectangle_section">
                <h2>What will students create?</h2>
                <img
                  class="rectangle-container"
                  src={rectangleImage}
                  alt="rectangle"/>
              </div>
            </div>
          </div>
        </div>
        <div className="col-lg-6">
          <div className="carousel-container">
            <div
              id="carouselIndicators"
              class="carousel slide"
              data-ride="carousel"
            >
              <ol className="carousel-dots carousel-indicators">
                <li
                  data-target="#carouselIndicators"
                  data-slide-to="0"
                  class="active"
                ></li>
                <li data-target="#carouselIndicators" data-slide-to="1"></li>
                <li data-target="#carouselIndicators" data-slide-to="2"></li>
                <li data-target="#carouselIndicators" data-slide-to="3"></li>
              </ol>
              <div className="carousel-inner">
                <div className="carousel-item active">
                  <img
                    class="lionking-slide"
                    src={lionKing}
                    alt="First slide"
                  />
                </div>
                <div className="carousel-item">
                  <img
                    class="baseball-slide"
                    src={baseball}
                    alt="Second slide"
                  />
                </div>
                <div className="carousel-item">
                  <img class="cartoon-slide" src={cartoon} alt="Third slide" />
                </div>
                <div className="carousel-item">
                  <img class="alan-slide" src={alan} alt="Fourth slide" />
                </div>
              </div>
              <div className="slide-buttons">
                <a
                  class="carousel-control-prev"
                  href="#carouselIndicators"
                  role="button"
                  data-slide="prev"
                >
                  <span
                    class="carousel-control-prev-icon"
                    aria-hidden="true"
                  ></span>
                  <span class="sr-only">Previous</span>
                </a>
                <a
                  class="carousel-control-next"
                  href="#carouselIndicators"
                  role="button"
                  data-slide="next">
                  <span
                    class="carousel-control-next-icon"
                    aria-hidden="true"
                  ></span>
                  <span class="sr-only">Next</span>
                </a>
              </div>
            </div>
          </div>
        </div>
      </div>
      </div>

      <div className="enroll">
        <p>ENROLL</p>
        <Button size="small" type="button" onClick={handleOpen}>
          SUBMIT
        </Button>
      </div>

      <div className="footer">
        <p>footer</p>
      </div>
    </div>
  );
};

export default Home;
